    # Exibe a barra de status dos robôs de batalha
    @staticmethod
    def draw_robots_status_bar(robot1, robot2):

        # Exibe a barra de status
        def draw():
            print("-" * SCREEN_WIDTH)
            print(f'|{robot1.name.center(SCREEN_WIDTH//2 +9)}|{robot2.name.center(SCREEN_WIDTH//2 + 8)}|')
            print("-" * SCREEN_WIDTH)
            print(f'| ESCUDO  : {render_shield_bar(robot1).ljust((SCREEN_WIDTH//4) + 10)}|{render_shield_bar_inverted(robot2).rjust((SCREEN_WIDTH//4) + 9)} :  ESCUDO |')
            print("-" * SCREEN_WIDTH)
            print(f'| VIDA    : {render_life_bar(robot1).ljust((SCREEN_WIDTH//4) + 10)}|{render_inverted_life_bar(robot2).rjust((SCREEN_WIDTH//4) + 9)} :    VIDA |')
            print("-" * SCREEN_WIDTH)
            print(f'| ENERGIA : {render_energy_bar(robot1).ljust((SCREEN_WIDTH//4) + 10)}|{render_energy_bar_inverted(robot2).rjust((SCREEN_WIDTH//4) + 9)} : ENERGIA |')
            print("-" * SCREEN_WIDTH)

        # Retorna uma barra de status
        def render_status_bar(bar_attribute, bar_icon):
            quantity_colored_squares = bar_attribute // 5
            bar = bar_icon * quantity_colored_squares
            bar += "⬜" * (20 - quantity_colored_squares)
            bar += f' {int(bar_attribute)}%'
            return bar

        # Retorna uma barra de status
        def render_inverted_status_bar(bar_attribute, bar_icon):
            quantity_colored_squares = bar_attribute // 5
            bar = f'{int(bar_attribute)}% '
            bar += "⬜" * (20 - quantity_colored_squares)
            bar += bar_icon * quantity_colored_squares
            return bar

        # Retorna a barra de vida do robô da esquerda para direita
        def render_life_bar(robot):
            square_color = "🟩" if robot.life > 60 else "🟧" if robot.life > 30 else "🟥"
            return render_status_bar(robot.life, square_color)

        # Retorna a barra de vida do robô da direita para esquerda
        def render_inverted_life_bar(robot):
            square_color = "🟩" if robot.life > 60 else "🟧" if robot.life > 30 else "🟥"
            return render_inverted_status_bar(robot.life, square_color)

        # Retorna a barra de escudo do robô da esquerda para direita
        def render_shield_bar(robot):
            return render_status_bar(robot.shield, "🟦")

        # Retorna a barra de escudo do robô da direita para a esquerda
        def render_shield_bar_inverted(robot):
            return render_inverted_status_bar(robot.shield, "🟦")

        # Retorna a barra de energia do robô da esquerda para direita
        def render_energy_bar(robot):
            return render_status_bar(robot.energy, "🟨")

        # Retorna a barra de energia do robô da direita para esquerda
        def render_energy_bar_inverted(robot):
            return render_inverted_status_bar(robot.energy, "🟨")

        draw()

    # Exibe a aparência dos robôs de batalha
    @staticmethod
    def draw_robots_appearance(robot1, robot2):
        i = 0
        while i < len(robot1.appearance) and i < len(robot2.appearance):
            print(f'|{robot1.appearance[i].center((SCREEN_WIDTH//2) + 13)}|{robot2.appearance[i].center((SCREEN_WIDTH//2) + 12)}|')
            i += 1
        print("-" * SCREEN_WIDTH)

    # Exibe as opções que o usuário pode escolher no menu de batalha
    @staticmethod
    def draw_battle_menu_options(robot):
        length = 28
        lines = {
            "LINE1": [
                f'[1] - {robot.attack_skills[0].name}'.center(length),
                f'[2] - {robot.attack_skills[1].name}'.center(length),
                f'[3] - {robot.attack_skills[2].name}'.center(length),
                f'[4] - CARREGAR ENERGIA'.center(length),
                f'[X] - SAIR DO COMBATE'.center(length),
            ],
            "LINE2": [
                f' Dano: {robot.attack_skills[0].damage}'.ljust(length),
                f' Dano: {robot.attack_skills[1].damage}'.ljust(length),
                f' Dano: {robot.attack_skills[2].damage}'.ljust(length),
                f''.ljust(length),
                f''.ljust(length)
            ],
            "LINE3": [
                f' Consumo de energia: {robot.attack_skills[0].energy_consumption}'.ljust(length),
                f' Consumo de energia: {robot.attack_skills[1].energy_consumption}'.ljust(length),
                f' Consumo de energia: {robot.attack_skills[2].energy_consumption}'.ljust(length),
                f''.ljust(length),
                f''.ljust(length)
            ]
        }
        lines = [
            f'|{lines["LINE1"][0]}|  |{lines["LINE1"][1]}|  |{lines["LINE1"][2]}|  |{lines["LINE1"][3]}|  |{lines["LINE1"][4]}|',
            f'|{lines["LINE2"][0]}|  |{lines["LINE2"][1]}|  |{lines["LINE2"][2]}|  |{lines["LINE2"][3]}|  |{lines["LINE2"][4]}|',
            f'|{lines["LINE3"][0]}|  |{lines["LINE3"][1]}|  |{lines["LINE3"][2]}|  |{lines["LINE3"][3]}|  |{lines["LINE3"][4]}|'
        ]
        bar_line = f' {"-" * (length + 2)}  {"-" * (length + 2)}  {"-" * (length + 2)}  {"-" * (length + 2)}  {"-" * (length + 2)} '
        for line in lines:
            print(f'|{bar_line.center(SCREEN_WIDTH-2)}|')
            print(f'|{line.center(SCREEN_WIDTH-2)}|')
        print(f'|{bar_line.center(SCREEN_WIDTH-2)}|')

    # Exibe a arte onde é possível visualizar a lore de um robô e suas habilidades
    @staticmethod
    def draw_robot_information(robot):
        print("-" * SCREEN_WIDTH)
        print(f'|{robot.name.center(SCREEN_WIDTH+8)}|')
        print("-" * SCREEN_WIDTH)
        for i in range(len(robot.appearance)):
            print(f'|{robot.appearance[i].center(SCREEN_WIDTH+12)}|')
        print("-" * SCREEN_WIDTH)
        print(f'|{"".center(SCREEN_WIDTH-2)}|')
        information = ""
        i = 0
        while i < len(robot.lore):
            information += robot.lore[i] + "  "
            for attack_skill in robot.attack_skills:
                if i < len(attack_skill.description):
                    information += attack_skill.description[i] + "  "
                else:
                    information += " " * (len(attack_skill.description[0])+2)
            print(f'|{information.center(SCREEN_WIDTH-2)}|')
            information = ""
            i += 1
        print(f'|{"".center(SCREEN_WIDTH-2)}|')
        print("-" * SCREEN_WIDTH)
